package org.bitcoins.core.serializers.transaction

import org.scalatest.{FlatSpec, MustMatchers}

/**
  * Created by chris on 11/28/16.
  */
class RawTransactionInputWitnessParserTest extends FlatSpec with MustMatchers {

  "RawTransactionInputWitnessParser" must "serialize symmetrically" in {
    val hex = ""
    val witness = RawTransactionInputWitnessParser.read(hex)
    witness.hex must be (hex)
  }
}
